css:
[data-bspk='list-item'] {
    display: flex;
    user-select: none;
    color: var(--foreground-neutral-on-surface);
    background-color: var(--surface-neutral-t1-base);
    height: 100%;
    overflow: hidden;
    min-height: var(--list-item-height);
    flex-direction: row;
    gap: var(--spacing-sizing-03);
    padding: var(--spacing-sizing-02);
    justify-items: stretch;
    border: unset;
    margin: unset;
    text-decoration: unset;
    width: 100%;

    &[data-width='hug'] {
        width: auto;
        max-width: 100%;
    }

    [data-pseudo='focus'] &,
    &:focus-visible,
    &:has(*:focus-visible) {
        outline: solid 2px var(--stroke-neutral-focus);

        [data-inner] {
            border-color: transparent;
        }
    }

    &:not([data-disabled], [data-readonly]) {
        &[data-action],
        &:is(label) {
            &[data-active],
            [data-pseudo='hover'] &,
            &:hover {
                background-color: var(--interactions-neutral-hover-opacity);
            }

            [data-pseudo='active'] &,
        // pressed state
        &:active {
                background-color: var(--interactions-neutral-press-opacity);
            }
        }
    }

    [data-leading],
    [data-item-label],
    [data-trailing] {
        min-height: 100%;
        display: flex;
        flex-direction: column;
        justify-content: space-around;
        flex: 1 1 0;
