css:
[data-bspk='otp-input'] {
    display: flex;
    flex-direction: row;
    justify-content: center;
    gap: var(--spacing-sizing-02);
    font: var(--font);
    width: fit-content;

    [data-digit] {
        display: flex;
        align-items: center;
        justify-content: center;
        border-radius: var(--radius-sm);
        border: 1px solid var(--stroke-neutral-base);
        flex-grow: 1;
        aspect-ratio: 1;
        width: var(--width);
        position: relative;
        outline: none;

        &:hover:not(:focus)::after {
            background: var(--interaction-hover-opacity);
            content: '';
            position: absolute;
            inset: 0;
            border-radius: var(--radius-sm);
        }

        &:active::after {
            background: var(--interaction-press-opacity);
            content: '';
            position: absolute;
            inset: 0;
            border-radius: var(--radius-sm);
        }

        &:focus:not(:active) {
            outline: solid 2px var(--stroke-neutral-focus);

            &:empty::before {
                // caret
                content: '';
                width: 2px;
                height: calc(var(--caret-height) - 8px);
                background: var(--stroke-neutral-high);
                animation: blink-caret 1s step-end infinite;

                @keyframes blink-caret {
                    0%,
                    100% {
