        height: var(--spacing-sizing-14);

        [data-snackbar-icon-text] {
            flex: 1 1 0; // allow to grow and shrink, but not overflow
            min-width: 0; // Prevent overflow from long content
            max-width: 100%;
            overflow: hidden;
            display: flex;
